mapper.select:
select
    m.template_name as manage_name,m.template_author as manage_author, d.template_name as diy_name,d.template_content as diy_content,d.extension_name as diy_extension,d.prefix_default as diy_prefix
from template_manage as m , template_diy as d
where  m.template_id =(SELECT template_id
                       FROM template_manage
                       WHERE template_name = #{name}
)and d.belong =(SELECT template_id
                FROM template_manage
                WHERE template_name = #{name}
)
